package com.april30;

public class DoWhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = 1 ; int sum = 0;
		do{
			if(num%2!=0){
				sum = sum+num;
				
					}
			num++;
			
		}
			while(num<=100);
		System.out.println(sum);
		
			
		}

		}

	
