package com.may13;

public class TwoDArray {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int[][] a = {{2,4,6},{1,3,4},{7,9,11}};
		for(int i = 0 ; i<3;i++){
			for(int j = 0 ; j<3;j++){
				System.out.println(a[i][j]);
			}
			System.out.println("loop for row");
			//System.out.println("\n");
		}


		}	

	}
